mapper.select:
select

from tb_news
where is_deleted=0

    AND (news_title like CONCAT('%',#{keyword},'%' ) or news_category_name like CONCAT('%',#{keyword},'%' ))


    AND news_status = #{newsStatus}


    AND news_category_id = #{newsCategoryId}

order by news_id desc

    limit #{start},#{limit}

select count(*) from tb_news
where is_deleted=0

    AND (news_title like CONCAT('%',#{keyword},'%' ) or news_category_name like CONCAT('%',#{keyword},'%' ))


    AND news_status = #{newsStatus}


    AND news_category_id = #{newsCategoryId}

select
from tb_news where news_id IN (SELECT news_id FROM tb_news_tag_relation WHERE tag_id = #{tagId}) AND news_status =1 AND is_deleted=0 order by news_id desc
limit #{start},#{limit}

mapper.update:
update tb_news
set is_deleted=1 where news_id in

    #{id}

update tb_news
set news_category_id = #{categoryId,jdbcType=INTEGER},
news_category_name = #{categoryName,jdbcType=VARCHAR}
where news_category_id in

    #{id}

and is_deleted =0
